/**
 * @param {any | null | undefined} variable
 * @returns {boolean}
 */
export function requireNonNull(variable: any | null | undefined): boolean;
/**
 * @param {any | null | undefined} variable
 * @returns {boolean}
 */
export function requireString(variable: any | null | undefined): boolean;
/**
 * @param {any | null | undefined} variable
 * @returns {boolean}
 */
export function requireUint8Array(variable: any | null | undefined): boolean;
/**
 * Utility Error Messages
 */
export const REQUIRE_NON_NULL_ERROR: "This value cannot be null | undefined.";
export const REQUIRE_STRING_ERROR: "This value must be a string.";
export const REQUIRE_UINT8ARRAY_ERROR: "This value must be a Uint8Array.";
